const Groq = require("groq-sdk");

const groq = new Groq({ apiKey: "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" });

async function llmaai(text) {
  const completion = await groq.chat.completions.create({
    messages: [
      {
        role: "user",
        content: text,
      },
    ],
    model: "llama3-8b-8192",
  });

  return {
    author: "Herza",
    status: 200,
    data: {
      model: "llama3-8b-8192",
      msg: completion.choices[0].message.content,
    },
  };
}

module.exports = { llmaai }